mapper.update:
UPDATE user set company_id=#{companyId}

  , email=#{email}


  , mobile=#{mobile}


  , account=#{account}


  , role=#{role}


    , name=#{name}


    , nick=#{nick}


    , sex=#{sex}


    , job=#{job}


    , identification=#{identification}


    , address=#{address}


    , info=#{info}


    , department_id=#{departmentId}


    , passwd=#{passwd}


    , company_id=#{companyId}


    , status=#{status}

where id=#{id} and company_id=#{companyId} and status != 3

mapper.select:
select count(1) from user where 1=2

    or mobile=#{mobile}


    or account = #{account}


    or email = #{email}
